// Get raw AI output safely
let rawText = $input.first().json.output || "";

// Normalize line breaks and remove unwanted formatting
rawText = rawText
  .replace(/\r/g, "")
  .replace(/\*/g, "")
  .replace(/#/g, "")
  .trim();

const lines = rawText.split("\n").map(l => l.trim()).filter(l => l !== "");

// Helper: safely extract numbers, treat empty or invalid as 0
function parseNumber(val) {
  if (!val || val === "") return 0;
  const cleaned = val.replace(/[^\d.-]/g, "");
  return cleaned === "" ? 0 : Number(cleaned);
}

let reports = {};
let currentSection = null;
let currentKPI = null;

for (let line of lines) {
  // Detect section header: "YTD Report: (Section Name)"
  let sectionMatch = line.match(/^YTD Report[:\s-]*\(?(.+?)\)?$/i);
  if (sectionMatch) {
    currentSection = sectionMatch[1].trim() || "Unknown";
    if (!reports[currentSection]) reports[currentSection] = {};
    currentKPI = null;
    continue;
  }

  // Detect KPI name line (starts with dash, may or may not have colon)
  let kpiMatch = line.match(/^-\s*([^:]+):?$/);
  if (kpiMatch && currentSection) {
    currentKPI = kpiMatch[1].trim();
    if (!reports[currentSection][currentKPI]) reports[currentSection][currentKPI] = {};
    continue;
  }

  // Detect sub-values: "- Key: Value"
  let valueMatch = line.match(/^-\s*([^:]+):\s*(.*)$/);
  if (valueMatch && currentSection && currentKPI) {
    let key = valueMatch[1].trim();
    let val = valueMatch[2].trim();
    reports[currentSection][currentKPI][key] = parseNumber(val);
  }
}

return [{ json: reports }];

// Flatten all parsed items
// If input is structured as {Section: {Metric: {subValues}}}, convert it
const items = [];

for (const entry of $input.all()) {
  const data = entry.json;
  for (const section in data) {
    for (const metric in data[section]) {
      const values = data[section][metric];
      items.push({
        section,
        metric,
        ytdTarget: values["YTD Target"] ?? 0,
        todaysAchievement: values["Today's Achievement"] ?? 0,
        ytdAchieved: values["YTD Achieved"] ?? 0,
        lastMonthSameDate: values["Last Month's Same Date"] ?? 0,
        remaining: values["Remaining"] ?? 0
      });
    }
  }
}

// Group by section
const grouped = {};
for (const item of items) {
  const section = item.section || "Unknown";
  if (!grouped[section]) grouped[section] = [];
  grouped[section].push(item);
}

// Helper: clean metric name
function getMetricName(str) {
  if (!str) return "-";
  return str.replace(/[:#*]/g, "").trim();
}

// Auto-detect section order (All Products first, then alphabetically)
let sectionOrder = Object.keys(grouped);
sectionOrder.sort((a, b) => {
  if (a.toLowerCase().includes("all products")) return -1;
  if (b.toLowerCase().includes("all products")) return 1;
  return a.localeCompare(b);
});

// Build HTML
let html = `<h2 style="text-align:center;">YTD Report Summary for iBOS</h2>`;

for (const section of sectionOrder) {
  html += `<h3 style="margin-top:20px;">${section}</h3>`;
  html += `
    <table border="1" cellspacing="0" cellpadding="6" 
           style="border-collapse:collapse; width:100%; font-family:sans-serif; font-size:13px;">
      <thead style="background:#f2f2f2;">
        <tr>
          <th>Metric</th>
          <th>YTD Target</th>
          <th>Today's Achievement</th>
          <th>YTD Achieved</th>
          <th>Last Month Same Date</th>
          <th>Remaining</th>
        </tr>
      </thead>
      <tbody>
  `;

  for (const row of grouped[section]) {
    html += `
      <tr>
        <td>${getMetricName(row.metric)}</td>
        <td style="text-align:right;">${row.ytdTarget}</td>
        <td style="text-align:right;">${row.todaysAchievement}</td>
        <td style="text-align:right;">${row.ytdAchieved}</td>
        <td style="text-align:right;">${row.lastMonthSameDate}</td>
        <td style="text-align:right;">${row.remaining}</td>
      </tr>
    `;
  }

  html += `</tbody></table>`;
}

return [{ json: { html } }];

let rawText = $input.first().json.output || '';
rawText = rawText.replace(/\r/g, '').trim();

const lines = rawText.split("\n").map(l => l.trim());
const data = [];
let currentMetric = null;
let buffer = [];

// Group metrics with their details
for (let line of lines) {
    if (line.startsWith("- ") && !line.includes("Target") && 
        !line.includes("Achievement") && !line.includes("Achieved") &&
        !line.includes("Last Month") && !line.includes("Remaining")) {

        // New metric line
        if (currentMetric) {
            data.push({ metric: currentMetric, text: buffer.join(" ") });
        }
        currentMetric = line.replace(/^-\s*/, '').replace(/:$/, '').trim();
        buffer = [];
    } else if (currentMetric) {
        buffer.push(line);
    }
}
// Push last metric
if (currentMetric) {
    data.push({ metric: currentMetric, text: buffer.join(" ") });
}

// Extract numeric fields (keep even if zero)
function getField(text, label) {
    const regex = new RegExp(label + ":\\s*([0-9,]*)", "i");
    const match = text.match(regex);
    return match ? match[1].replace(/,/g, '') || "0" : "0";
}

// âœ… Fixed list of 8 metrics in order
const allowedMetrics = [
    "Sales Value (b)",
    "Number of Sales (Deals)",
    "Proposal Value (b)",
    "Number of Proposals Sent",
    "Number of Meetings",
    "Number of Client Visits",
    "Number of Leads Generated",
    "Number of Calls Made"
];

let cleanedData = allowedMetrics.map(m => {
    let found = data.find(d => d.metric === m);
    if (!found) {
        return {
            metric: m,
            target: "0",
            today: "0",
            achieved: "0",
            lastMonth: "0",
            remaining: "0"
        };
    }
    return {
        metric: found.metric,
        target: getField(found.text, "YTD Target"),
        today: getField(found.text, "Today's Achievement"),
        achieved: getField(found.text, "YTD Achieved"),
        lastMonth: getField(found.text, "Last Month Same Date"),
        remaining: getField(found.text, "Remaining")
    };
});

return [{ json: { report: cleanedData } }];

const items = $input.all().map(i => i.json.report).flat(); // Flatten all reports

// Optional: group by a "section" if you have one, else put all under "Overall Summary"
const sectionName = "All Products"; 
const grouped = { [sectionName]: items };

// Build HTML output
let html = `<h2 style="text-align:center; font-family:sans-serif;">Overall Summary OEM</h2>`;

for (const section of Object.keys(grouped)) {
    html += `<h3 style="margin-top:20px; font-family:sans-serif;">${section}</h3>`;
    html += `
        <table border="1" cellspacing="0" cellpadding="6" 
               style="border-collapse:collapse; width:100%; font-family:sans-serif;">
          <thead style="background:#f2f2f2;">
            <tr>
              <th>Metric</th>
              <th>YTD Target</th>
              <th>Today Achievement</th>
              <th>YTD Achieved</th>
              <th>Last Month Same Date</th>
              <th>Remaining</th>
            </tr>
          </thead>
          <tbody>
    `;

    for (const row of grouped[section]) {
        html += `
            <tr>
              <td>${row.metric || '-'}</td>
              <td>${row.target || '-'}</td>
              <td>${row.today || '-'}</td>
              <td>${row.achieved || '-'}</td>
              <td>${row.lastMonth || '-'}</td>
              <td>${row.remaining || '-'}</td>
            </tr>
        `;
    }

    html += `</tbody></table>`;
}

return [{ json: { html } }];

let combinedHtml = "";

for (const item of items) {
  combinedHtml += item.json.html + "<br><br>";
}

return [{
  json: {
    emailBody: combinedHtml
  }
}];
